(function(){tinymce.create('tinymce.plugins.IcodePlugin',{init:function(ed,url){ed.addCommand('mceIcode',function(){ed.windowManager.open({file:url+'/icode.htm',width:500+parseInt(ed.getLang('icode.delta_width',0)),height:300+parseInt(ed.getLang('icode.delta_height',0)),inline:1},{plugin_url:url})});ed.addButton('icode',{title:'I-Code',cmd:'mceIcode',image:url+'/img/logo.gif'})},getInfo:function(){return{longname:'Icode',author:'Jeison Frasson',authorurl:'http://www.lliure.com.br',infourl:'http://www.lliure.com.br/pagina/icode.html',version:tinymce.majorVersion+"."+tinymce.minorVersion}}});tinymce.PluginManager.add('icode',tinymce.plugins.IcodePlugin)})();